import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'
import {Navbar, Nav, Button, Container, Row, Col, Form, Modal} from "react-bootstrap";
import {useState} from "react";
import {Navigate, useNavigate} from "react-router-dom";
import api from "../api";

export default function Forms(route, method) {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        setLoading(true);
        e.preventDefault();

        try {
            const res = await api.post(route, {username, password});
            if (method === "login") {
                localStorage.setItem('token', res.data.access);
                localStorage.setItem('token', res.data.refresh);
                
                <Navigate to={'/profile'}/>
            }
        } catch (error) {
            alert((error))
        } finally {
            setLoading(false)
        }
    }

    const name = method === "login" ? "Login" : "Register";

    return (
        <Form onSubmit={handleSubmit} className={'form-container form'}>
            <h1>{name}</h1>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email"
                              value={username}
                              onChange={(e) => setUsername(e.target.value)}
                              placeholder="Enter email"
                />

                <Form.Label>Password</Form.Label>
                <Form.Control type="password"
                              value={password}
                              onChange={(e) => setPassword(e.target.value)}
                              placeholder="Enter password"
                />
                <Button variant="primary" type="submit">{name}</Button>
            </Form.Group>


        </Form>
    )
}
