import React, {useEffect, useState} from "react";
import axios from "axios";
import {Button, Container, Form} from "react-bootstrap";
import {redirect} from "react-router-dom";

export default function Login() {
    const serverUrl = 'http://localhost:8000/'

    const [isCsrf, setIsCsrf] = useState(null)
    const [isLogin, setIsLogin] = useState('')
    const [isPassword, setIsPassword] = useState('')
    const [isError, setIsError] = useState(null)
    const [isAuth, setIsAuth] = useState(false)
    const [name, setname] = useState('')
    const [sessionId, setsession] = useState('')

    useEffect(() => {
        getCsrfs()
        // getSession()
    }, [])

    const isResponseOk = (res) => {
        if (!(res.status >= 200 && res.status <= 299)) {
            throw Error(res.statusText);
        }
    }

    const getSession = () => {
        const sessionid = document.cookie.split('; ')
            .find(row => row.startsWith('sessionid=')).split('=')[1];
        if (sessionid === 'undefined') {
            console.log(document.cookie)
        } else {
            setsession(sessionid);
            setIsAuth(true)
            console.log(document.cookie)
        }

    }

    const getCsrfs = () => {


        try {
            const csrftoken = document.cookie.split('; ')
                .find(row => row.startsWith('csrftoken=')).split('=')[1];
            setIsCsrf(csrftoken);
            setIsAuth(true)

        } catch (e) {
            console.error(e)
        }

    }
    // Если необходимо авторизоваться, запрашиваем CSRF-токен у сервера
    const getCSRF = () => {
        axios.get(serverUrl + 'api/csrf/', {withCredentials: true})
            .then((res) => {
                isResponseOk(res);

                const csrfToken = res.headers.get('X-CSRFToken');
                setIsCsrf(csrfToken);
                return csrfToken;
            })
            .catch((err) => console.error(err))
    }


    // Полученный CSRF-токен пихаем в заголовок и отправляем серверу
    const login = () => {
        const data = {username: isLogin, password: isPassword}
        fetch('http://localhost:8000/api/login/', {
            credentials: 'include',
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-CSRFToken': isCsrf,
            },
        })
            .then((res) => {
                isResponseOk(res);
                setIsAuth(true);
                setname(data.username);
                setIsError(null);
                userInfo()


            })
            .catch((err) => {
                console.error(err);
                setIsError("Неверные данные")
            });
    }
    const logout = () => {
        fetch('http://localhost:8000/api/logout/', {
            credentials: 'include',
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8',
                'X-CSRFToken': isCsrf,
            },
        })
            .then((response) => {
                setIsAuth(false);
                setIsCsrf(null);
                document.cookie('csrftoken=', '');
            })
            .catch((error) => {
                setIsError('Ошибка при выходе');
            })


    }

    const userInfo = () => {
        const data = {username: name}
        axios.post(serverUrl + "api/user_info/")
            .then((res) => {
                console.log(res, 555);
            })
            .catch((err) => {
                console.log(err.error);
            });
    }


    function changePassword(e) {
        setIsPassword(e.target.value)
    }

    function changeLogin(e) {
        setIsLogin(e.target.value)
    }

    function submitForm(e) {
        e.preventDefault()
        login()
    }

    return (

        <Container>

            {
                isAuth === false ?
                    <Form>
                        <Form.Label htmlFor="login">Логин</Form.Label>
                        <Form.Control
                            type="text"
                            name="login"
                            id="login"
                            className='{style.field}'
                            onChange={changeLogin}
                            value={isLogin}
                        />

                        <Form.Label htmlFor="password">Пароль</Form.Label>
                        <Form.Control
                            type="password"
                            name="password"
                            id="password"
                            className='{style.field}'
                            onChange={changePassword}
                            value={isPassword}
                        />

                        {
                            isError ? <div className='{style.error}'>{isError}</div> : null
                        }

                        <Button type="submit" href={'/login'} onClick={submitForm}
                                className='{style.sendBtn}'> Войти</Button>
                    </Form>
                    :
                    <div>
                        <Form className='{style.authStatus}'>
                            Вы -
                            <Form.Label className='{style.name}'>
                                {
                                    isCsrf ? ' ' + name : ' неавторизованы'
                                }
                            </Form.Label>

                        </Form>
                        <Button type="submit" href={'/login'} onClick={logout} className='{style.logoutBtn}'>Выйти
                        </Button>
                    </div>
            }
        </Container>
    )
}
