import axios from "axios";
import {ACCESS_TOKEN} from "./constants"
const api = axios.create({
  baseURL: "http://localhost:8000",
});

api.interceptors.request.use(
    config => {
        console.log(config)
        return config;
    },
    (error) => {
    return Promise.reject(error);
    }
)
console.log(api);
export default api;
