import React, {useEffect, useState} from "react";
import axios from "axios";
import {Button, Container, Form} from "react-bootstrap";

export default function Login() {
    const serverUrl = 'http://localhost:8000/'

    const [isCsrf, setIsCsrf] = useState(null)
    const [isPhone, setIsisPhone] = useState('')
    const [isPassword, setIsPassword] = useState('')
    const [isError, setIsError] = useState(null)
    const [isAuth, setIsAuth] = useState(false)

    const [IsName, setIsName] = useState('')

    // При первой загрузке страницы мы спрашиваем, авторизован ли пользователь
    useEffect(() => {
        getSession()
    }, [])

    const isResponseOk = (res) => {
        if (!(res.status >= 200 && res.status <= 299)) {
            throw Error(res.statusText);
        }
    }

    // Если необходимо авторизоваться, запрашиваем CSRF-токен у сервера
    const getCSRF = () => {
        axios.get(serverUrl + 'api/csrf/', {withCredentials: true})
            .then((res) => {
                isResponseOk(res)
                const csrfToken = res.headers.get('X-CSRFToken')

                setIsCsrf(csrfToken)
            })
            .catch((err) => console.error(err))
    }

    // withCredentials:true - аналогия директивы credentials='include'
    const getSession = () => {
        console.log(axios.get(serverUrl + "api/session/", {withCredentials: true}))
            //
            // .then((res) => {
            //
            //     if (res.data.isAuthenticated) {
            //         setIsName(res.data.name)
            //         setIsAuth(true)
            //         return
            //     }
            //
            //     setIsAuth(false)
            //     getCSRF()
            // })
            // .catch(
            //     err => console.error(err)
            // )
    }



    const register = () => {
        getCSRF()
        const data = {name: IsName, password: isPassword, phone: isPhone}
        axios.post(serverUrl + "api/register/", data, {
            withCredentials: true,
            headers: {
                "Content-Type": "application/json",
                "X-CSRFToken": isCsrf,
            }
        })
            .then((res) => {
                isResponseOk(res)
                setIsAuth(true)
                setIsName('')
                setIsPassword('')
                setIsError(null)

                userInfo()
            })
            .catch((err) => {

                console.log(err);
                setIsError("qweНеверные данные")
            });
    }
    const userInfo = () => {
        axios.get(serverUrl + "api/user_info/", {
            withCredentials: true,
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => {
                console.log("Вы авторизованы как: " + res.data.name);
                setIsName(res.data.name)
            })
            .catch((err) => {
                if (err.status === 401) console.log(err.error);
            });
    }
    const logout = () => {
        axios.get(serverUrl + "api/logout", {withCredentials: true})
            .then((res) => {
                isResponseOk(res)
                setIsAuth(false);
                getCSRF();
            })
            .catch(err => console.error(err));
    }


    const killAllSessions = () => {
        axios.get(serverUrl + "api/kill_all_sessions/", {
            withCredentials: true,
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => {
                isResponseOk(res)
                console.log(res.data.detail)
            })
            .catch((err) => {
                console.log(err);
            });
    }

    function changePassword(e) {
        setIsPassword(e.target.value)
    }


    function submitForm(e) {
        e.preventDefault()
        register()
    }

    function changeName(e) {
        setIsName(e.target.value)
    }

    function changePhone(e) {
        setIsisPhone(e.target.value)
    }

    function submitForm(e) {
        e.preventDefault()
        register()
    }

    return (
        <Container>{
            !isAuth ?
                <Form className={'card-registration'}>
                    <Form.Label htmlFor="name">ФИО</Form.Label>
                    <Form.Control
                        type="name"
                        name="name"
                        id="name"
                        className='{style.field}'
                        onChange={changeName}
                        value={IsName}
                    />
                    <Form.Label htmlFor="phone">Логин</Form.Label>
                    <Form.Control
                        type="tel"
                        name="phone"
                        id="phone"
                        className='{style.field}'
                        onChange={changePhone}
                        value={isPhone}
                    />

                    <Form.Label htmlFor="password">Пароль</Form.Label>
                    <Form.Control
                        type="password"
                        name="password"
                        id="password"
                        className='{style.field}'
                        onChange={changePassword}
                        value={isPassword}
                    />

                    {
                        isError ? <div className='{style.error}'>{isError}</div> : null
                    }

                    <Button type="submit" value='Войти' onClick={submitForm} className='{style.sendBtn}'/>
                </Form>
                :
                <div className='{style.btnContainer}'>
                    <input type="submit" value='Выйти' onClick={logout} className='{style.logoutBtn}'/>
                    <input type="submit" value='Убить все сессии' onClick={killAllSessions}
                           className='{style.killAllSessionsBtn}'/>
                </div>

        }
        </Container>
    )
}
