import React, {useEffect, useState} from "react";
import axios from "axios";
import {Button, Container, Form} from "react-bootstrap";
import {redirect} from "react-router-dom";

export default function Login() {
    const serverUrl = 'http://localhost:8000/'

    const [isCsrf, setIsCsrf] = useState(null)
    const [isLogin, setIsLogin] = useState('')
    const [isPassword, setIsPassword] = useState('')
    const [isError, setIsError] = useState(null)
    const [isAuth, setIsAuth] = useState(false)
    const [name, setname] = useState('')

    useEffect(() => {
        getSession()
    }, [])

    const isResponseOk = (res) => {
        if (!(res.status >= 200 && res.status <= 299)) {
            throw Error(res.statusText);
        }
    }

    const getCsrfs = () => {

        try {
            const sessionid = document.cookie.split('; ')
                .find(row => row.startsWith('sessionid=')).split('=')[1]
            if (sessionid) {
                setIsAuth(true)
            } else {
                const csrftoken = document.cookie.split('; ')
                    .find(row => row.startsWith('csrftoken=')).split('=')[1];
                setIsCsrf(csrftoken);
                console.log(csrftoken)
            }
console.log(isAuth)

        } catch (e) {
            console.error(e)
        }

    }
    // Если необходимо авторизоваться, запрашиваем CSRF-токен у сервера
    const getCSRF = () => {
        axios.get(serverUrl + 'api/csrf/', {withCredentials: true})
            .then((res) => {
                isResponseOk(res);

                const csrfToken = res.headers.get('X-CSRFToken');
                setIsCsrf(csrfToken);
                return csrfToken;
            })
            .catch((err) => console.error(err))
    }


    // Полученный CSRF-токен пихаем в заголовок и отправляем серверу
    const login = () => {
        const data = {username: isLogin, password: isPassword, isAuthenticated:true}
        axios.post(serverUrl + "api/login/", data)
            .then((res) => {
                isResponseOk(res);
                setIsAuth(true);
                getSession()
                setname(data.username);
                setIsError(null);
                redirect('/login/');
                document.cookie('csrftoken=', isCsrf)
                console.log(isCsrf)
                // document.cookie('sessionid=', )
            })
            .catch((err) => {
                console.error(err);
                setIsError("Неверные данные")
            });
    }


    const logout = () => {
        fetch('http://localhost:8000/api/logout/', {
            credentials: 'include',
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8',
            },
        })
            .then((response) => {
                setIsAuth(false);
                setIsError(null);
                setIsCsrf(null);

                redirect('/')

            })
            .catch((error) => {
                console.log(error);
                setIsError('Ошибка при выходе');
            })


    }



    function changePassword(e) {
        setIsPassword(e.target.value)
    }

    function changeLogin(e) {
        setIsLogin(e.target.value)
    }

    function submitForm(e) {
        e.preventDefault()
        login()
    }
    const getSession = () => {
      fetch('http://localhost:8000/api/session/', {
        credentials: 'include',
        method: 'POST',
        })
          .then((res) => {
              if (res.data.isAuthenticated) {
                  setIsAuth(true)
                  return
              }

              setIsAuth(false)
              getCSRF()
          })
          .catch(err => console.error(err))
        }
    return (

        <Container>

            {
                isAuth === false ?
                    <Form>
                        <Form.Label htmlFor="login">Логин</Form.Label>
                        <Form.Control
                            type="text"
                            name="login"
                            id="login"
                            className='{style.field}'
                            onChange={changeLogin}
                            value={isLogin}
                        />

                        <Form.Label htmlFor="password">Пароль</Form.Label>
                        <Form.Control
                            type="password"
                            name="password"
                            id="password"
                            className='{style.field}'
                            onChange={changePassword}
                            value={isPassword}
                        />

                        {
                            isError ? <div className='{style.error}'>{isError}</div> : null
                        }

                        <Button type="submit" value='Войти' onClick={submitForm}
                                className='{style.sendBtn}'> Войти</Button>
                    </Form>
                    :
                    <div className='{style.authStatus}'>
                        Вы -
                        <span className='{style.name}'>
                        {
                            isAuth ? ' ' + name : ' неавторизованы'
                        }
                        </span>
                        <div className='{style.btnContainer}'>
                            <input type="submit" value='Выйти' onClick={logout} className='{style.logoutBtn}'/>
                        </div>
                    </div>
            }
        </Container>
    )
}
