import React, {useEffect, useState} from "react";
import axios from "axios";
import {Button, Container, Form} from "react-bootstrap";

export default function Login() {
    const serverUrl = 'http://localhost:8000/'

    const [isCsrf, setIsCsrf] = useState(null)
    const [isLogin, setIsLogin] = useState('')
    const [isPassword, setIsPassword] = useState('')
    const [isError, setIsError] = useState(null)
    const [isAuth, setIsAuth] = useState(false)
    const [name, setname] = useState('')
    const [userId, setUserId] = useState(null)

    useEffect(() => {

        // getCSRF()
        // getSession()
    }, [])

    const isResponseOk = (res) => {
        if (!(res.status >= 200 && res.status <= 299)) {
            throw Error(res.statusText);
        }
    }

    // Если необходимо авторизоваться, запрашиваем CSRF-токен у сервера
    const getCSRF = () => {
        axios.get(serverUrl + 'api/csrf/', {withCredentials: true})
            .then((res) => {
                isResponseOk(res)

                const csrfToken = res.headers.get('X-CSRFToken')
                setIsCsrf(csrfToken)
            })
            .catch((err) => console.error(err))
    }


    // Полученный CSRF-токен пихаем в заголовок и отправляем серверу
    const login = () => {
        const data = {username: isLogin, password: isPassword}
        console.log(data)
        axios.post(serverUrl + "api/login/", data)
            .then((res) => {
                isResponseOk(res)
                setIsAuth(true)
                // setIsLogin('')
                // setIsPassword('')
                setIsError(null)
                getCSRF()

                userInfo()
            })
            .catch((err) => {
                console.error(err);
                setIsError("Неверные данные")
            });
    }

    const next_login = (data) => {
        data.isAuthenticated = true;
        // getSession(data)

    }

    const logout = () => {
        axios.post(serverUrl + "api/logout/")
        setIsAuth(false);
        getCSRF();

    }

    const userInfo = () => {
        const data = {username: isLogin, password: isPassword}

        axios.post(serverUrl + "api/user_info/",data)
            .then((res) => {
                console.log("Вы авторизованы как: " + isLogin);
            })
            .catch((err) => {
                if (err.status === 401) console.log(err.error);
            });
    }

    const killAllSessions = () => {
        axios.get(serverUrl + "api/kill_all_sessions/", {
            withCredentials: true,
            headers: {
                "Content-Type": "application/json",
            },
        })
            .then((res) => {
                isResponseOk(res)
                console.log(res.data.detail)
            })
            .catch((err) => {
                console.log(err);
            });
    }

    function changePassword(e) {
        setIsPassword(e.target.value)
    }

    function changeLogin(e) {
        setIsLogin(e.target.value)
    }

    function submitForm(e) {
        e.preventDefault()
        login()
    }

    return (

        <Container>

            <div className='{style.authStatus}'>
                Вы -
                <span className='{style.name}'>
            {
                isAuth ? ' ' + name : ' неавторизованы'
            }
            </span>
            </div>

            {
                !isCsrf ?
                    <Form>
                        <Form.Label htmlFor="login">Логин</Form.Label>
                        <Form.Control
                            type="text"
                            name="login"
                            id="login"
                            className='{style.field}'
                            onChange={changeLogin}
                            value={isLogin}
                        />

                        <Form.Label htmlFor="password">Пароль</Form.Label>
                        <Form.Control
                            type="password"
                            name="password"
                            id="password"
                            className='{style.field}'
                            onChange={changePassword}
                            value={isPassword}
                        />

                        {
                            isError ? <div className='{style.error}'>{isError}</div> : null
                        }

                        <Button type="submit" value='Войти' onClick={submitForm}
                                className='{style.sendBtn}'> Войти</Button>
                    </Form>
                    :

                    <div className='{style.btnContainer}'>
                        <input type="submit" value='Выйти' onClick={logout} className='{style.logoutBtn}'/>
                        <input type="submit" value='Убить все сессии' onClick={killAllSessions}
                               className='{style.killAllSessionsBtn}'/>
                    </div>

            }
        </Container>
    )
}

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';

// Получение CSRF токена из cookie
function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
      const cookie = cookies[i].trim();
      if (cookie.substring(0, name.length + 1) === (name + '=')) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 2));
        break;
      }
    }
  }
  return cookieValue;
}

axios.defaults.headers.common['X-CSRFToken'] = getCookie('csrftoken');
