import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css'
import {Navbar, Nav, Button, Container, Row, Col, Form, Modal} from "react-bootstrap";
import {useState} from "react";
import {Navigate, useNavigate} from "react-router-dom";
import api, {apiSetHeader} from "../api";
import {ACCESS_TOKEN, REFRESH_TOKEN} from "../constants";

export default function Forms(route, method) {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [loading, setLoading] = useState(false);
    const name = method === "login" ? "Login" : "Register";

    const handleSubmit = async (e) => {
        setLoading(true);
        e.preventDefault();

        try {
            const data = await api.post(route, {
                username,
                password
            });
            if (method === "login") {
                localStorage.setItem('jwt', data.access);
                apiSetHeader('Authorization', `Bearer ${data.access}`);
                localStorage.setItem(ACCESS_TOKEN, data.access);
                localStorage.setItem(REFRESH_TOKEN, data.refresh);

                <Navigate to={'/profile'}/>
            } else {
                <Navigate tzo={'/login'}/>
            }

        } catch (error) {
            alert(error);
        } finally {
            setLoading(false);
        }


    }


    return (
        <Form onSubmit={handleSubmit} className={'form-container form'}>
            <h1>{name}</h1>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email"
                              value={username}
                              onChange={(e) => setUsername(e.target.value)}
                              placeholder="Enter email"
                />

                <Form.Label>Password</Form.Label>
                <Form.Control type="password"
                              value={password}
                              onChange={(e) => setPassword(e.target.value)}
                              placeholder="Enter password"
                />

                <Button variant="primary" type="submit">{name}</Button>
            </Form.Group>


        </Form>
    )
}
